// Child component loads its own data
function RoundCourseTeeName({ round }: { round: MaybeLoaded<Round> | null }) {
  const [courseTeeName, setCourseTeeName] = useState<string>("Loading...");

  useEffect(() => {
    if (!round?.$isLoaded) return;

    const loadData = async () => {
      if (round.$jazz.has("course") && round.$jazz.has("tee")) {
        await round.$jazz.ensureLoaded({
          resolve: {
            course: true,
            tee: true,
          },
        });
        setCourseTeeName(`${round.course?.name} • ${round.tee?.name}`);
      }
    };
    loadData();
  }, [round]);

  return <Text>{courseTeeName}</Text>;
}

