// CORRECT: Lightweight selector + useMemo for expensive work
const project = useCoState(Project, projectId, {
  resolve: { tasks: { $each: true } },
  select: (project) => ({
    tasks: project.tasks,
    taskIds: project.tasks.map(t => t.$jazz.id)  // Lightweight - just extract IDs
  })
});

// useMemo only recomputes when taskIds change
const sortedTasks = useMemo(() => {
  return project?.tasks
    .slice(0)
    .sort(someExpensiveSortFunction);
}, [project?.taskIds]);

